query FileReferenceQuery($fileReferenceId: Int) {
  fileReferenceById(fileReferenceId: $fileReferenceId) {
    fileReferenceId
    fileReferenceName
    fileStatusId
    workerId
    createDate
    updateDate
    defaultGeographicLocaleId
    isTemporaryFile
    fileStatus {
      fileStatusId
      description
    }
    titleSearchOrigination {
      titleEventId
      titleSearchOriginationId
      orderDate
      orderReference
      fileReferenceId
    }
    branchLocation {
      branchLocationId
      isInternal
      accountNumber
      description
    }
    geographicLocale {
      geographicLocaleId
      geographicLocaleTypeId
      localeAbbreviation
      localeName
      parentGeographicLocaleId
    }
    searches {
      searchId
      searchReference
      fileReferenceId
      instrumentFilters
      lrsSearch
      inclMortgageeShortForm
      hidden
      searchFromDate
      searchThruDate
      nameSearchParameters {
        searchId
        legalEntityNameId
        legalFilter
        ownerBuyerRelationshipId
        firstNamePctOfLikeness
        flipNames
        lastNamePctOfLikeness
        searchGiNames
        searchGranteeNames
        searchGiNames
        searchSimilarSoundingNames
      }
      policyTitleStatusReport {
        searchId
        titleStatusReportCode
        titleStatusReportNumber
      }
      parentSearch {
        searchId
        parentSearchId
      }
      searchStatus {
        searchStatusId
        description
      }
      grantorCertRange {
        certificationRangeId
        fromDate
        fromOrDocumentId
        toDate
        toOrDocumentId
      }
      giCertRange {
        certificationRangeId
        fromDate
        fromOrDocumentId
        toDate
        toOrDocumentId
      }
      geographicCertRange {
        certificationRangeId
        fromDate
        fromOrDocumentId
        toDate
        toOrDocumentId
      }
      searchType {
        searchTypeId
        description
      }
      geographicLocale {
        geographicLocaleId
        geographicLocaleTypeId
        localeAbbreviation
        localeName
        parentGeographicLocaleId
      }
      searchWarnings {
        searchId
        searchWarningId
        unparsedSearchWarning
        searchWarningType {
          searchWarningTypeId
          description
          searchWarningHelp {
            searchWarningTypeId
            helpText
          }
        }
      }
      subdivisionPlattedLegals {
        platReferenceId
        searchId
        subdivisionLevelId
        plattedLegal {
          platReferenceId
          subdivisionLevelId
          subdivisionLevels {
            subdivisionLevelId
            level1
            level2
            level3
          }
          policyPlattedLegalMqls {
            platReferenceId
            policyId
            subdivisionLevelId
          }
          titleEventPlattedLegalMqls {
            platReferenceId
            subdivisionLevelId
            titleEventId
          }
        }
      }
      acreageSectionLegals {
        sectionBreakdownCodeId
        unplattedReferenceId
        searchId
      }
      acreageGovtLotLegals {
        governmentLotId
        unplattedReferenceId
        searchId
        governmentLotLegals {
          governmentLotId
          unplattedReferenceId
          governmentLot {
            governmentLotId
            governmentLotNumber
          }
        }
      }
    }
    chainOfTitles {
      chainOfTitleId
      fileReferenceId
      chainOfTitleSearches {
        chainOfTitleId
        searchId
        search {
          searchId
          searchReference
          parentSearchId
          geographicLocaleId
          grantorCertRangeId
          giCertRangeId
          instrumentFilters
          lrsSearch
          inclMortgageeShortForm
          hidden
          searchTypeId
          searchFromDate
          searchThruDate
          searchStatusId
        }
      }
      chainOfTitleNotes {
        chainOfTitleId
        chainOfTitleNotesId
        userId
        timeStamp
        message
      }
      chainOfTitleItems {
        chainOfTitleId
        chainOfTitleItemId
        titleEvent {
          titleEventId
          currentExamStatusTypeId
          originalExamStatusTypeId
          titleEventStatusAssignorId
          titleEventTypeId
          amount
          additionalInformation
          titleEventDate
          createDate
          tag
        }
        chainOfTitleCategory {
          chainOfTitleCategoryId
          description
        }
      }
    }
    fileReferenceNotes {
      fileReferenceId
      fileReferenceNotesId
      message
      userId
      timeStamp
    }
    worksheet {
      worksheetId
      fileReferenceId
      worksheetItems {
        worksheetId
        titleEventId
        sequence
        titleEvent {
          titleEventId
          currentExamStatusTypeId
          originalExamStatusTypeId
          titleEventStatusAssignorId
          titleEventTypeId
          amount
          additionalInformation
          titleEventDate
          createDate
          tag
        }
      }
      policyWorksheetItems {
        policyId
        worksheetId
        sequence
        policy {
          policyId
          policyType
          policyNumber
          policyRestrictionTypeId
          effectiveDate
          createDate
          memberNumber
          insuredAmount
          imageAvailable
          titleBaseIndicator
          systemUpdateDate
          userUpdateDate
        }
      }
    }
  }
}
